import "./styles.css";
import {
  useState,
  useEffect,
  useMemo,
  Children,
  useLayoutEffect,
  useRef,
} from "react";

const Slider = ({ children }) => {
  const containerRef = useRef();
  const [current, setCurrent] = useState(1);
  const [translateX, setTranslateX] = useState(0);
  const slides = useMemo(() => {
    if (children.length > 1) {
      let items = Children.map(children, (child, index) => (
        <li key={index} className="slide">
          {child}
        </li>
      ));
      return [
        <li key={children.lenght + 1} className="slide">
          {children[children.lenght - 1]}
        </li>,
        ...items,
        <li key={children.lenght + 2} className="slide">
          {children[0]}
        </li>,
      ];
    }

    return <li className="slide">{children[0]}</li>;
  }, [children]);

  useLayoutEffect(() => {
    setTranslateX(containerRef.current.clientWidth * current);
  }, []);

  return (
    <section className="slider">
      <ul
        ref={containerRef}
        className="container"
        style={{ transform: `translate3d(${-translateX}%, 0, 0)` }}
      >
        {slides}
      </ul>
    </section>
  );
};
export default Slider;
