import "./style.css";
import { useEffect, useState } from "react";
import sliderData from "./indexData";
import filmBorder from "../../assets/images/filmborder.png";

const Slider = () => {
  const [animacion, setAnimacion] = useState("");

  const frames = () => {
    setAnimacion("moveright 200 linear");
    setTimeout(setAnimacion(""), 800);
  };

  useEffect(() => {
    setInterval(frames, 1000);
  }, []);

  return (
    <section className="slider">
      <div className="sliderInner" style={{ animation: animacion }}>
        {sliderData.map((image, index) => {
          return (
            <img
              src={filmBorder}
              key={index}
              alt={image}
              style={{
                backgroundImage: `url(${image})`,
                backgroundPosition: "center",
                backgroundSize: "95% 69%",
                backgroundRepeat: "no-repeat",
              }}
            />
          );
        })}
      </div>
    </section>
  );
};
export default Slider;
