import "./style.css";
import sliderData from "./indexData";
import filmBorder from "../../assets/images/filmborder.png";
import { useEffect } from "react";

const Slider = () => {
  const transitionDuration = 1000;
  let finalTransition = -100;

  return (
    <section className="slider">
      <div
        className="sliderInner"
        style={{
          animation: `moveright ${transitionDuration} ease-in-out infinite`,
          "--final": `${finalTransition}%`,
          "--init": "0",
        }}
      >
        {sliderData.map((image, index) => {
          return (
            <img
              src={filmBorder}
              key={index}
              alt={image}
              style={{
                backgroundImage: `url(${image})`,
                backgroundPosition: "center",
                backgroundSize: "95% 69%",
                backgroundRepeat: "no-repeat",
              }}
            />
          );
        })}
      </div>
    </section>
  );
};
export default Slider;
