import { useState } from "react";
import { Link } from "react-router-dom";

const HeaderMenu = ({ setContactShow }) => {
  const colorSelecionado = "orange";
  const [inicioColor, setInicioColor] = useState(colorSelecionado);
  const [seriesColor, setSeriesColor] = useState("");
  const [cineColor, setCineColor] = useState("");
  const [teatroColor, setTeatroColor] = useState("");
  const [musicaColor, setMusicaColor] = useState("");
  const [fotosColor, setFotosColor] = useState("");
  const [cvColor, setCvColor] = useState("");
  const [contactoColor, setContactoColor] = useState("");

  return (
    <ul>
      <li>
        <Link
          to="/"
          style={{
            color: inicioColor,
          }}
          onClick={() => {
            setContactShow(true);
            setInicioColor(colorSelecionado);
            setSeriesColor("");
            setCineColor("");
            setTeatroColor("");
            setMusicaColor("");
            setFotosColor("");
            setCvColor("");
            setContactoColor("");
          }}
        >
          INICIO
        </Link>
      </li>
      <li>
        <Link
          to="/series"
          style={{
            color: seriesColor,
          }}
          onClick={() => {
            setContactShow(true);
            setInicioColor("");
