            setCineColor("");
            setTeatroColor("");
            setMusicaColor("");
            setFotosColor("");
            setCvColor("");
            setContactoColor("");
          }}
        >
          SERIES
        </Link>
      </li>
      <li>
        <Link
          to="/cinetv"
          style={{
            color: cineColor,
          }}
          onClick={() => {
            setContactShow(true);
            setInicioColor("");
            setSeriesColor("");
            setCineColor(colorSelecionado);
            setTeatroColor("");
            setMusicaColor("");
            setFotosColor("");
            setCvColor("");
            setContactoColor("");
          }}
        >
          CINE/TV
        </Link>
      </li>
      <li>
        <Link
          to="/teatro"
          style={{
            color: teatroColor,
          }}
          onClick={() => {
            setContactShow(true);
            setInicioColor("");
            setSeriesColor("");
            setCineColor("");
            setTeatroColor(colorSelecionado);
            setMusicaColor("");
            setFotosColor("");
            setCvColor("");
            setContactoColor("");
          }}
        >
          TEATRO
        </Link>
      </li>
      <li>
        <Link
          to="/teatro"
          style={{
            color: musicaColor,
          }}
          onClick={() => {
            setContactShow(true);
            setInicioColor("");
            setSeriesColor("");
            setCineColor("");
            setTeatroColor("");
            setMusicaColor(colorSelecionado);
            setFotosColor("");
            setCvColor("");
            setContactoColor("");
          }}
        >
          MÚSICA
        </Link>
      </li>
      <li>
        <Link
          to="/fotos"
          style={{
            color: fotosColor,
          }}
          onClick={() => {
            setContactShow(true);
            setInicioColor("");
            setSeriesColor("");
            setCineColor("");
            setTeatroColor("");
            setMusicaColor("");
            setFotosColor(colorSelecionado);
            setCvColor("");
            setContactoColor("");
          }}
        >
          FOTOS
        </Link>
      </li>
      <li>
        <Link
          to="/cv"
          style={{
            color: cvColor,
          }}
          onClick={() => {
            setContactShow(true);
            setInicioColor("");
            setSeriesColor("");
            setCineColor("");
            setTeatroColor("");
            setMusicaColor("");
            setFotosColor("");
            setCvColor(colorSelecionado);
            setContactoColor("");
          }}
        >
          CV
        </Link>
      </li>
      <li>
        <Link
          to="/contacto"
          style={{
            color: contactoColor,
          }}
          onClick={() => {
            setContactShow(false);
            setInicioColor("");
            setSeriesColor("");
            setCineColor("");
            setTeatroColor("");
            setMusicaColor("");
            setFotosColor("");
            setCvColor("");
            setContactoColor(colorSelecionado);
          }}
        >
          CONTACTO
        </Link>
      </li>
    </ul>
  );
};
export default HeaderMenu;
