import "./style.css";
import { useEffect, useState } from "react";
import sliderData from "./indexData";
import filmBorder from "../../assets/images/filmborder.png";

const Slider = () => {
  const slider = document.querySelector(".sliderInner");
  const frames = () => {
    const animacion = slider.animate(
      [{ transform: "TranslateX(-100%)" }, { transform: "TranslateY(0)" }],
      {
        easing: "linear",
        iterations: 1,
        duration: 200,
      }
    );
    return animacion.finished;
  };
  useEffect(() => {
    setInterval(frames, 1000);
  }, []);

  return (
    <section className="slider">
      <div className="sliderInner">
        {sliderData.map((image, index) => {
          return (
            <img
              src={filmBorder}
              key={index}
              alt={image}
              style={{
                backgroundImage: `url(${image})`,
                backgroundPosition: "center",
                backgroundSize: "95% 69%",
                backgroundRepeat: "no-repeat",
              }}
            />
          );
        })}
      </div>
    </section>
  );
};
export default Slider;
