import "./style.css";
import sliderData from "./indexData";
import filmBorder from "../../assets/images/filmborder.png";
import { useState, useEffect } from "react";

const Slider = () => {
  const [translate, setTranslate] = useState(0);
  const [transitionDuration, setTransitionDuration] = useState(400);

  useEffect(() => {
    setTimeout(() => {
      const transitionEnd = () => {
        if (translate <= -300) {
          setTransitionDuration(0);
          setTranslate(0);
        } else {
          setTransitionDuration(400);
          setTranslate(translate - 100);
        }
      };
      document.addEventListener("transitionend", transitionEnd);
    }, 5000);
  }, [translate]);

  return (
    <section className="slider">
      <div
        className="sliderInner"
        style={{
          transform: `translateX(${translate}%)`,
          transitionDuration: `${transitionDuration}ms`,
        }}
      >
        {sliderData.map((image, index) => {
          return (
            <img
              src={filmBorder}
              key={index}
              alt={image}
              style={{
                backgroundImage: `url(${image})`,
                backgroundPosition: "center",
                backgroundSize: "95% 69%",
                backgroundRepeat: "no-repeat",
              }}
            />
          );
        })}
      </div>
    </section>
  );
};
export default Slider;
