import "./style.css";
import sliderData from "./indexData";
import filmBorder from "../../assets/images/filmborder.png";
import { useState, useEffect } from "react";

const Slider = () => {
  const transitionDuration = 1000;
  const [finalPositionAnimation, setFinalPositionAnimation] = useState(-100);
  const [initialPositionAnimation, setInitialPositionAnimation] = useState(0);
  console.log(finalPositionAnimation);

  useEffect(() => {
    setTimeout(() => {
      setFinalPositionAnimation(finalPositionAnimation - 100);
      setInitialPositionAnimation(initialPositionAnimation - 100);
    }, 1000);
  }, [finalPositionAnimation]);

  return (
    <section className="slider">
      <div
        className="sliderInner"
        style={{
          animation: `moveright ${transitionDuration}ms ease-in-out infinite`,
          "--final": `${finalPositionAnimation}%`,
          "--init": `${initialPositionAnimation}%`,
        }}
      >
        {sliderData.map((image, index) => {
          return (
            <img
              src={filmBorder}
              key={index}
              alt={image}
              style={{
                backgroundImage: `url(${image})`,
                backgroundPosition: "center",
                backgroundSize: "95% 69%",
                backgroundRepeat: "no-repeat",
              }}
            />
          );
        })}
      </div>
    </section>
  );
};
export default Slider;
